/* height: 25rem; */
/* } */
.package-inner-image {
  position: relative;
  /* height: 0; */
  height: 25rem;
  width: 100%;
  /* padding-top: 45%; */
  background-repeat: no-repeat;
  background-size: cover !important;
  background-position: 50% !important;
  /* background-position: center; */
  /* border-radius: 10px; */
  /* background-color: #c9c9c9; */
  background: rgba(0, 0, 0, 0.4);
  background: linear-gradient(
    to right,
    rgb(0, 0, 0) 0%,
    rgba(0, 0, 0, 0.6) 45%,
    rgba(0, 0, 0, 0) 80%
  );
}

/* .package-inner-image:before {
  content: "";
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 2;
} */

.tour-title {
  font-size: 2.375rem;
  line-height: 2.5rem;
}

.serif {
  font-family: "Gentium Basic", serif;
}

.tour-title-card {
  width: 100%;
  height: auto;
  position: absolute;
  display: block;
  left: 0;
  bottom: 0;
  text-shadow: 1px 1px 5px #000;
  padding: 3.75rem 1.25rem 0.9375rem;
  z-index: 3;
  color: #fff;
  /* -webkit-print-color-adjust: exact;
  print-color-adjust: exact; */
}

@media (min-width: 970px) {
  .tour-title-card {
    padding-left: 2.1875rem;
  }
}

@media (min-width: 744px) {
  .tour-title-card {
    width: 50%;
    height: auto;
    bottom: 0.9375rem;
    padding: 0 1.25rem 0.9375rem 1.5625rem;
  }
}

.nav_tabs {
  background-color: #846644;
}

.nav_tabs nav-tabs nav-link {
  font-size: 16px;
  font-weight: 400;
  margin: 3rem;
}

.nav-tabs .nav-link {
  border-radius: 0;
  color: #000;
  font-family: "Gentium Basic", serif;
}
.nav-link.active {
  color: #3f230b;
  border-color: #adadad;
  background: #adadad;
  border-radius: 0;
  font-size: 20px;
  font-weight: 500;
}

.card {
  border-radius: 0;
}

.title {
  /* margin-left: -1.25rem;
  margin-right: -1.25rem; */
  padding: 0 1.25rem 0.9375rem;
  border-bottom: 0.0625rem solid #d9d9d9;
  position: relative;
}

.title strong {
  padding-top: 0.25rem;
  margin-bottom: 0.5rem;
  font-size: 1.125rem;
  line-height: 1.5rem;
}
.title strong h1 {
  line-height: 2rem;
  font-size: 1.75rem;
  font-weight: 600;
  letter-spacing: 0.03125rem;
}

.our-color {
  color: #3f230b;
}

ul li {
  list-style: none;
  font-size: 16px;
  position: relative;
  margin-bottom: 5px;
}

li {
  list-style: none;
}
li,
p {
  line-height: 1.5 !important;
  /* font-weight: 300; */
}

ul li.exclude:before,
ul li.include:before {
  display: inline-block;
  width: 1.5em;
  top: 2px;
  left: -23px;
  position: absolute;
}
ul li.include:before {
  content: "✓";
  color: #3f230b;
}

.quote_card {
  position: sticky;
  top: 12%;
}

.btn-brown {
  background: #3f230b;
  color: #fff;
}
.form-control:focus {
  color: #212529;
  background-color: #fff;
  border-color: #3f230b;
  outline: 0;
  box-shadow: 0 0 0 0.25rem #846644;
}

.btn-block {
  width: 100%;
}

