.body {
  font-family: "Gentium Basic", serif;
}
.section-header h1 {
  font-size: 30px;
  color: #846644;
  text-align: center;
  font-weight: 500;
  position: relative;
  padding-bottom: 15px;
}

.section-header h1:before {
  content: "";
  position: absolute;
  display: block;
  width: 120px;
  height: 1px;
  background: #ddd;
  bottom: 1px;
  left: calc(50% - 60px);
}

.section-header h1:after {
  content: "";
  position: absolute;
  display: block;
  width: 40px;
  height: 3px;
  background: #3f230b;
  bottom: 0;
  left: calc(50% - 20px);
}
.title {
  /* margin-left: -1.25rem;
  margin-right: -1.25rem; */
  padding: 0 1.25rem 0.9375rem;
  border-bottom: 0.0625rem solid #d9d9d9;
  position: relative;
}

.title strong {
  padding-top: 0.25rem;
  margin-bottom: 0.5rem;
  font-size: 1.125rem;
  line-height: 1.5rem;
}
.title strong h1 {
  line-height: 2rem;
  font-size: 1.75rem;
  font-weight: 600;
  letter-spacing: 0.03125rem;
}
.card {
  border-radius: 0;
}

.btn-brown {
  background: #3f230b;
  color: #fff;
}

.form-check {
  margin-bottom: 0;
  min-height: auto;
}

.form-check-input {
  position: relative;
  width: 1.125rem;
  height: 1.125rem;
  background-color: #fff;
  border: 0.125rem solid #3f230b;
}

.form-check-input {
  width: 1em;
  height: 1em;
  margin-top: 0.3em;
  vertical-align: top;
  background-repeat: no-repeat;
  background-position: 50%;
  background-size: contain;
  border: 1px solid #3f230b;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  /* -webkit-print-color-adjust: exact; */
  /* color-adjust: exact; */
  transition: background-color 0.15s ease-in-out,
    background-position 0.15s ease-in-out, border-color 0.15s ease-in-out,
    box-shadow 0.15s ease-in-out;
}

.form-check-input[type="checkbox"]:checked {
  background-color: #3f230b;
}
.form-check-input[type="checkbox"]:focus {
  border-color: #3f230b;
}

.form-check-input[type="checkbox"] {
  border-radius: 0.125rem;
}
.form-check .form-check-input {
  margin-left: -1.79rem;
}
.form-check-input[type="checkbox"] {
  border-radius: 0.25em;
}
.form-check .form-check-input {
  float: left;
  margin-left: -1.5em;
}
.filter_button {
  display: none;
}
.fileterMobileHeader {
  display: none;
}
@media screen and (max-device-width: 640px) {
  .filters {
    display: none;
  }

  .filter_button {
    display: block;
  }

  .filterContents {
    margin-top: 40px;
    padding-top: 15px;
    padding-bottom: 15px;
  }
  .card {
    border: 0px;
  }
  .title {
    border-bottom: 0px;
    padding-left: 0px;
    padding-right: 0px;
  }

  /* #filters {
    display: none;
  } */
  .mobile_view {
    display: block;
    background-color: #fff;
    position: fixed;
    height: 100%;
    width: 100%;
  }

  .filter_close {
    background-color: #e2e1e1;
    padding: 0.625rem 1.25rem;
    position: absolute;
    width: 100%;
    top: 0 !important;
    left: 0;
    box-sizing: border-box;
    font-size: 1.25rem;
    z-index: 200;
    box-shadow: 0px px 2px rgba(0, 0, 0, 0.18);
  }

  .filters-active-mobile {
    background: rgb(255, 255, 255);
    position: fixed;
    height: 100%;
    width: 100%;
    z-index: 10000;
    overflow: scroll;
    top: 0px;
    margin-top: 0px;
  }
  .wrapper {
    width: 100vw;
    height: 100vh;
  }
  .fileterMobileHeader {
    top: 0;
    width: 100%;
    position: absolute;
    display: block;
    background-color: #ddd;
    box-shadow: 3px 3px 6px black;
  }
  .mobile_filter_header {
    font-size: 25px;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
}

.btn-primary {
  --bs-btn-color: #fff;
  --bs-btn-bg: #3f230b;
  --bs-btn-border-color: #3f230b;
  --bs-btn-hover-color: #fff;
  --bs-btn-hover-bg: #846644;
  --bs-btn-hover-border-color: #846644;
  --bs-btn-focus-shadow-rgb: 49, 132, 253;
  --bs-btn-active-color: #fff;
  --bs-btn-active-bg: #846644;
  --bs-btn-active-border-color: #846644;
  --bs-btn-active-shadow: inset 0 3px 5px rgba(0, 0, 0, 0.125);
  --bs-btn-disabled-color: #fff;
  --bs-btn-disabled-bg: #3f230b;
  --bs-btn-disabled-border-color: #3f230b;
}
.btn:hover {
  color: var(--bs-btn-hover-color);
  background-color: #3f230b;
  border-color: #3f230b;
}
.our-color {
  color: #3f230b;
}
